Level6.prototype.dispose = function(all) {
    if (this.loaded) {
        if (all) {
            this.loaded = false;
            this.coins = null;
        }
        this.enemies = null;
        this.tiles = null;
        try {
            if (this.music !== null) {
                this.music.stop();
            }
        } catch (e) {}
        this.music = null;
    }
};

